var e = 12,
  n = 1;
function setFontSize(e) {
	$('body').addClass('sizer')

  e < min && (e = min),
    e > max && (e = max),
    $("body *").css({ "font-size": parseInt(e) + 3 + "px" });
}
function makeNormal() {
  $("html").removeClass("blackAndWhite blackAndWhiteInvert");
}
function makeBlackAndWhite() {
  makeNormal(), $("html").addClass("blackAndWhite");
}
function makeBlackAndWhiteDark() {
  makeNormal(), $("html").addClass("blackAndWhiteInvert");
}
function saveFontSize(e) {}
function changeSliderText(e, n) {
  var t = Math.round(Math.abs((100 / (max - min)) * (n - min)));
  $("#" + e)
    .prev(".sliderText")
    .children(".range")
    .text(t);
}
function savezoomSizer(e) {
  $.session("zoomSizer", e, { path: "/" });
}
function changeSliderTextZoom(e, n) {
  var t = Math.round(Math.abs(100 - 20 * (maxzoom - n)));
  $("#" + e)
    .prev(".sliderZoom")
    .children(".range")
    .text(t);
}

var min = 8,
  max = 20;
$(document).ready(function () {
  $(".no-propagation").click(function (e) {
    e.stopPropagation();
  }),
    $(".appearance .spcNormal").click(function () {
      makeNormal();
    }),
    $(".appearance .spcWhiteAndBlack").click(function () {
      makeBlackAndWhite();
    }),
    $(".appearance .spcDark").click(function () {
      makeBlackAndWhiteDark();
    }),
    $("#fontSizer").slider({
      min: min,
      max: max,
      range: "min",
      slide: function (e, n) {
        setFontSize(n.value), changeSliderText("fontSizer", n.value);
      },
      change: function (e, n) {
        /*saveFontSize(n.value)*/
      },
    });

  void 0 !== e &&
    ($("#fontSizer").slider("value", e),
    // setFontSize(e),
    changeSliderText("fontSizer", e)),
    $("#zoomSizer").slider({
      min: minzoom,
      max: maxzoom,
      range: "min",
      slide: function (e, n) {
        // setzoomSizer(n.value), 
		changeSliderTextZoom("zoomSizer", n.value);
      },
      change: function (e, n) {},
    });
  void 0 !== n &&
    ($("#zoomSizer").slider("value", n),
    // setzoomSizer(n),
    changeSliderTextZoom("zoomSizer", n));
});
var minzoom = 0,
  maxzoom = 5;
